graphql.query:
query ListProducts(
  $page: Int = 0
  $size: Int = 20
  $sortBy: String = "createdDate"
  $sortDir: String = "desc"
  $nameLike: String
  $avail: Boolean
) {
  products(page: $page, size: $size, sortBy: $sortBy, sortDir: $sortDir, nameLike: $nameLike, avail: $avail) {
    total
    page
    size
    nodes {
      id
      name
      avail
      createdDate
      modifiedDate
    }
  }
}
